mapper.update:
update h_dbc_profiles
set profile_desc=#{profileDesc},
    updated_at=#{updatedAt}
where profile_name = #{profileName}

mapper.select:
select a.profile_name AS "profileName", a.profile_desc AS "profileDesc", a.created_at AS "createdAt", a.updated_at AS "updatedAt", b.username AS "username" from h_dbc_profiles a,h_dbc_acc_profiles b where a.profile_name=b.profile_name
and a.profile_name like #{profileNameLike}
and a.profile_desc like #{profileDescLike}
order by a.profile_name

select profile_name      AS "profileName",
       count(config_key) AS "configNum"
from h_dbc_config
where app = #{app}
  and username = #{username}
  and service_id = #{serviceId}
group by profile_name

mapper.delete:
delete
from h_dbc_config
where app = #{app}
  and username = #{username}
  and service_id = #{serviceId}
  and profile_name = #{profileName}

delete
from h_dbc_acc_profiles
where app = #{app}
  and username = #{username}
  and profile_name = #{profileName}
